import { CheckCircle } from '@mui/icons-material';
import { Avatar, Box, Card, CardContent, CardMedia, Paper, Typography } from '@mui/material';
import { Stack } from '@mui/system';
import { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { fetchFromAPI } from '../../API/fetchFromAPI';

import { videoBasic } from '../../API/basic';

// import { videoBasic } from '../../API/basic';
function Search({ data }) {
    const { id } = useParams();
    const [results, setResults] = useState([]);

    useEffect(() => {
        fetchFromAPI(`search?q=${id}&part=snippet,id&regionCode=US&order=date`).then((data) => setResults(data.items));
    }, [id]);
    console.log(results);
    return (
        <Stack mt="16px" ml="24px" gap={2}>
            <Card sx={{ width: '100%', height: '180px' }}>
                <Link
                    to={data?.id?.videoId ? `/videos/${data.id.videoId}` : '/videos/vanvi'}
                    style={{ textDecoration: 'none ', color: '#000' }}
                >
                    <CardMedia
                        component="img"
                        alt={videoBasic.title}
                        sx={{ width: '320px', height: '180px' }}
                        image={videoBasic.urlBasic}
                        // ref={videoRef}
                        // muted
                    />
                </Link>
                <CardContent sx={{ p: 0 }}>
                    <Link
                        to={data?.id?.videoId ? `/videos/${data.id.videoId}` : '/videos/vanvi'}
                        style={{ textDecoration: 'none ', color: '#000' }}
                    >
                        <Typography
                            variant="body1"
                            fontWeight={500}
                            color="#000"
                            fontSize="16px"
                            sx={{ height: '32px', lineHeight: '16px', overflow: 'hidden', mt: 1.2 }}
                        >
                            title o day
                        </Typography>
                    </Link>
                    <Link
                        to={data?.snippet?.channelId ? `/channel/${data.snippet.channelId}` : '/videos/vanvi'}
                        style={{ textDecoration: 'none ' }}
                    >
                        <Typography variant="body1" fontWeight={300} color="#000" fontSize="16px">
                            channel o day
                            <CheckCircle sx={{ color: 'blue', fontSize: '12px', ml: '5px' }} />
                        </Typography>
                    </Link>
                    <Typography variant="body1" fontWeight={300} color="#000" fontSize="12px">
                        18N Lượt xem 2 ngày trước
                    </Typography>
                </CardContent>
            </Card>
        </Stack>
    );
}

export default Search;
